	public static String encrypt(String plaintext, Key key) throws Exception{
		cipher.init(Cipher.ENCRYPT_MODE, key);
		byte[] bytes = plaintext.getBytes("UTF-8");
		byte[] encrypted = blockCipher(bytes,Cipher.ENCRYPT_MODE);
		return Base64.getEncoder().encodeToString(encrypted);
	}
	
	public static String decrypt(String encrypted, Key key) throws Exception{
		cipher.init(Cipher.DECRYPT_MODE, key);
		byte[] bts = Base64.getDecoder().decode(encrypted.getBytes());
		byte[] decrypted = blockCipher(bts,Cipher.DECRYPT_MODE);
		return new String(decrypted,"UTF-8").trim();
	}
	
	private static byte[] blockCipher(byte[] bytes, int mode) throws IllegalBlockSizeException, BadPaddingException{
		byte[] scrambled = new byte[0];
		byte[] toReturn = new byte[0];
		int length = (mode == Cipher.ENCRYPT_MODE)? 100 : 128;
		byte[] buffer = new byte[length];
		for (int i=0; i< bytes.length; i++){
			if ((i > 0) && (i % length == 0)){
				scrambled = cipher.doFinal(buffer);
				toReturn = append(toReturn,scrambled);
				int newlength = length;

				if (i + length > bytes.length) {
					 newlength = bytes.length - i;
				}
				buffer = new byte[newlength];
			}
			buffer[i%length] = bytes[i];
		}
		scrambled = cipher.doFinal(buffer);
		toReturn = append(toReturn,scrambled);
		return toReturn;
	}
	
	private static byte[] append(byte[] prefix, byte[] suffix){
		byte[] toReturn = new byte[prefix.length + suffix.length];
		for (int i=0; i< prefix.length; i++){
			toReturn[i] = prefix[i];
		}
		for (int i=0; i< suffix.length; i++){
			toReturn[i+prefix.length] = suffix[i];
		}
		return toReturn;
	}
